package main

import (
	"fmt"

	"github.com/gen2brain/iup-go/iup"
)

func main() {
	iup.Open()
	defer iup.Close()

	iup.ImageRGBA(32, 32, imgGopher).SetHandle("imgGopher")

	mltline := iup.MultiLine()
	iup.SetAttribute(mltline, "FORMATTING", "YES")
	iup.SetAttribute(mltline, "SIZE", "300x200")
	iup.SetAttribute(mltline, "EXPAND", "YES")

	iup.SetAttribute(mltline, "VALUE",
		"Rich Text Formatting\n"+
			"This line has italic and strikeout.\n"+
			"Third Line - Large Centered Title\n"+
			"Big Big Big Words Here\n"+
			"Fifth line with colors and underline.\n"+
			"Superscript and Subscript test: E=mc2 H2O\n"+
			"Line with indentation and spacing.\n"+
			"Bullet list and numbering test.\n"+
			"Image inline test: [IMG1] and scaled [IMG2] done.\n"+
			"Visit IUP Documentation for more info.")

	// Title - large, centered, bold
	formattag := iup.User()
	iup.SetAttribute(formattag, "FONTSIZE", "24")
	iup.SetAttribute(formattag, "ALIGNMENT", "CENTER")
	iup.SetAttribute(formattag, "WEIGHT", "BOLD")
	iup.SetAttribute(formattag, "SELECTION", "1,1:1,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Italic + strikeout
	formattag = iup.User()
	iup.SetAttribute(formattag, "ITALIC", "YES")
	iup.SetAttribute(formattag, "STRIKEOUT", "YES")
	iup.SetAttribute(formattag, "SELECTION", "2,1:2,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Double underline + bold + blue on part of line 3 ("Large Centered")
	formattag = iup.User()
	iup.SetAttribute(formattag, "UNDERLINE", "DOUBLE")
	iup.SetAttribute(formattag, "WEIGHT", "BOLD")
	iup.SetAttribute(formattag, "FGCOLOR", "0 0 255")
	iup.SetAttribute(formattag, "SELECTION", "3,14:3,28")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Font face + size on line 4
	formattag = iup.User()
	iup.SetAttribute(formattag, "FONTFACE", "Courier")
	iup.SetAttribute(formattag, "FONTSIZE", "20")
	iup.SetAttribute(formattag, "SELECTION", "4,1:4,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Red foreground on the first part of line 5
	formattag = iup.User()
	iup.SetAttribute(formattag, "FGCOLOR", "255 0 0")
	iup.SetAttribute(formattag, "SELECTION", "5,1:5,22")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Yellow background + underline on the second part of line 5
	formattag = iup.User()
	iup.SetAttribute(formattag, "BGCOLOR", "255 255 0")
	iup.SetAttribute(formattag, "UNDERLINE", "SINGLE")
	iup.SetAttribute(formattag, "SELECTION", "5,23:5,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Superscript on "2" in "mc2" (col 37)
	formattag = iup.User()
	iup.SetAttribute(formattag, "RISE", "SUPERSCRIPT")
	iup.SetAttribute(formattag, "FONTSCALE", "SMALL")
	iup.SetAttribute(formattag, "SELECTION", "6,37:6,38")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Subscript on "2" in "H2O" (col 40)
	formattag = iup.User()
	iup.SetAttribute(formattag, "RISE", "SUBSCRIPT")
	iup.SetAttribute(formattag, "FONTSCALE", "SMALL")
	iup.SetAttribute(formattag, "SELECTION", "6,40:6,41")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Indent + spacing on line 7
	formattag = iup.User()
	iup.SetAttribute(formattag, "INDENT", "30")
	iup.SetAttribute(formattag, "SPACEBEFORE", "10")
	iup.SetAttribute(formattag, "SPACEAFTER", "10")
	iup.SetAttribute(formattag, "SELECTION", "7,1:7,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Bullet numbering on line 8
	formattag = iup.User()
	iup.SetAttribute(formattag, "NUMBERING", "BULLET")
	iup.SetAttribute(formattag, "SELECTION", "8,1:8,50")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Hyperlink on "IUP Documentation" (line 10, cols 7-23)
	formattag = iup.User()
	iup.SetAttribute(formattag, "LINK", "https://www.tecgraf.puc-rio.br/iup/")
	iup.SetAttribute(formattag, "SELECTION", "10,7:10,24")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	iup.SetCallback(mltline, "LINK_CB", iup.TextLinkFunc(func(ih iup.Ihandle, url string) int {
		fmt.Printf("Link clicked: %s\n", url)
		return iup.DEFAULT
	}))

	dlg := iup.Dialog(iup.Vbox(mltline))
	iup.SetAttribute(dlg, "TITLE", "Rich Text Formatting")
	iup.SetAttribute(dlg, "MARGIN", "10x10")
	iup.SetAttribute(dlg, "SHRINK", "YES")

	iup.Map(dlg)

	// Image at natural 32x32 size - replace [IMG2] first
	formattag = iup.User()
	iup.SetAttribute(formattag, "IMAGE", "imgGopher")
	iup.SetAttribute(formattag, "SELECTION", "9,38:9,44")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	// Image scaled to 16x16 - replace [IMG1]
	formattag = iup.User()
	iup.SetAttribute(formattag, "IMAGE", "imgGopher")
	iup.SetAttribute(formattag, "WIDTH", "16")
	iup.SetAttribute(formattag, "HEIGHT", "16")
	iup.SetAttribute(formattag, "SELECTION", "9,20:9,26")
	iup.SetAttributeHandle(mltline, "ADDFORMATTAG", formattag)

	iup.ShowXY(dlg, iup.CENTER, iup.CENTER)

	iup.MainLoop()
}

